INSERT INTO `his_config_info` VALUES (2, 71, 'kylincloud-gateway.yml', 'DEFAULT_GROUP', '', 'server:\n  port: 18000\n# Spring\nspring:\n  application:\n    name: kylincloud-gateway\n  cloud:\n    gateway:\n      routes:\n        # 平台服务\n        - id: kylincloud-platform\n          uri: lb://kylincloud-platform\n          predicates:\n            - Path=/platform/**\n          filters:\n            - CacheRequestFilter\n            - CaptchaFilter\n            - StripPrefix=1\n        # 流程服务\n        - id: kylincloud-flow\n          uri: lb://kylincloud-flow\n          predicates:\n            - Path=/flow/**\n          filters:\n            - StripPrefix=1\n        # 监控服务\n        - id: kylincloud-monitor\n          uri: lb://kylincloud-monitor\n          predicates:\n            - Path=/monitor/**\n          filters:\n            - StripPrefix=1\n        # demo\n        - id: kylincloud-demo\n          uri: lb://kylincloud-demo\n          predicates:\n            - Path=/demo/**\n          filters:\n            - StripPrefix=1\n\n      #全局CORS配置\n      globalcors:\n        cors-configurations:\n          \'[/**]\':\n            allowedOrigins: \"*\"\n            allowedMethods: \"*\"\n            allowedHeaders: \"*\"\n\n    sentinel:\n      # 取消控制台懒加载\n      eager: true\n      transport:\n        # 控制台地址\n        dashboard: 127.0.0.1:18718\n      # nacos配置持久化\n#      datasource:\n#        ds1:\n#          nacos:\n#            server-addr: 127.0.0.1:18848\n#            dataId: sentinel-kylincloud-gateway\n#            groupId: DEFAULT_GROUP\n#            data-type: json\n#            rule-type: flow\n\n# actuator监控\nmanagement:\n  #  server:\n  #    # 设置监控服务端口,如果没写默认就是服务器的端口\n  #    port: 8081\n  endpoints:\n    # 设置端点是否可用 默认只有shutdown可用\n    enabled-by-default: true\n    web:\n      # 设置是否暴露端点 默认只有health和info可见\n      exposure:\n        # 包括所有端点\n        include: \"*\" # 注意需要添加引号\n        # 排除端点\n        exclude: shutdown\n  endpoint:\n    health:\n      show-deta